import React from "react";
import { useState } from "react";
import { Socket } from "socket.io-client";

const Chat = ({ socket }) => {
  const [message, setMessage] = useState("");

  // 서버에 데이터를 전달 !
  const sendMessage = async () => {
    socket.emit("message", message);
  };

  return (
    <div className="flex items-center justify-center h-full">
      <div className="w-1/3 h-[600px] bg-white relative">
        <div className="w-full h-16 bg-gray-700 flex items-center p-3">
          <div className="w-12 h-12 bg-white rounded-full"></div>
        </div>
        <div className="w-full h-[400px] overflow-y-auto">
          <div className="flex justify-end">
            <div className="w-2/3 h-12 p-2 bg-blue-600 text-white m-2 rounded-xl rounded-br-none ">
              <div>Dename Message!</div>
              <div className="w-full flex justify-end text-xs">
                Berkant KAYA - 12.04.2023
              </div>
            </div>
          </div>
          <div className="w-2/3 h-12 p-2 bg-green-600 text-white m-2 rounded-xl rounded-br-none ">
            <div>Dename Message!</div>
            <div className="w-full flex justify-end text-xs">
              Berkant KAYA - 12.04.2023
            </div>
          </div>
        </div>
        <div className="absolute bottom-0 left-0 w-full">
          <input
            value={message}
            onChange={(e) => setMessage(e.target.value)}
            className="w-3/4 h-12 border p-3 outline-none"
            type="text"
            placeholder="message send"
          />
          <button
            onClick={sendMessage}
            className="w-1/4 bg-indigo-600 text-white h-12 hover-opacity-70"
          >
            SEND
          </button>
        </div>
      </div>
    </div>
  );
};

export default Chat;
