import React from "react";
import { useEffect } from "react";
import { useState } from "react";
import {
  doc,
  setDoc,
  collection,
  serverTimestamp,
  query,
  onSnapshot,
  orderBy,
} from "firebase/firestore";
import db from "./firebaseConfig/firebaseConfig";

const Chat = ({ socket, room, username }) => {
  const [message, setMessage] = useState("");
  const [messageList, setMessageList] = useState([]);
  // firebase db 변수 생상
  // store의 데이터베이스 짜임 : Collention(폴더) - Document(파일) - field(data)
  const chatsRef = collection(db, "Message");

  useEffect(() => {
    socket.on("messageReturn", (data) => {
      console.log(data);
      setMessageList((prev) => [...prev, data]);
    });
  }, [socket]);

  useEffect(() => {
    const q = query(chatsRef, orderBy("createdAt", "asc"));

    // onSnapshot : 실시간으로 정보 가져오기
    const unsub = onSnapshot(q, (querySnapshot) => {
      const fireChats = [];
      // querySnapshot : Collection으로부터 Query,snapshot을 통해 받아온 데이터 타입
      // Collection으로부터 특정 Document들을 가져왔기 때문에 하나씩 풀어야 한다
      // docs : QuerySnapshot의 내부 데이터 리스트에 접근
      querySnapshot.forEach((doc) => {
        fireChats.push(doc.data()); // data : 실제 데이터가 들어있음
      });
      setChats([...fireChats]);
    });
    return () => {
      unsub();
    };
  }, []);

  // 소켓에 message를 담아 서버에 전달 !
  const sendMessage = async () => {
    const messageContent = {
      username: username,
      message: message,
      room: room,
      date:
        new Date(Date.now()).getHours() +
        ":" +
        new Date(Date.now()).getMinutes(),
    };
    // messageContent 값이 먼저 정의 된 후 메세지 전달.
    await socket.emit("message", messageContent);
    // 메세지 리스트에 방금 보낸 메세지도 함께 추가.
    setMessageList((prev) => [...prev, messageContent]);
    setMessage("");
  };

  const onKeyPress = (e) => {
    if (e.key === "Enter") {
      sendMessage();
    }
  };

  console.log("messageList", messageList);

  return (
    <div className="flex items-center justify-center h-full">
      <div className="w-1/3 h-[600px] bg-white relative">
        <div className="w-full h-16 bg-gray-700 flex items-center p-3">
          <div className="w-12 h-12 bg-white rounded-full"></div>
        </div>
        <div className="w-full h-[400px] overflow-y-auto">
          {messageList &&
            messageList.map((msg, i) => (
              <div
                className={`${
                  username === msg.username ? "flex justify-end" : ""
                }`}
              >
                <div
                  className={`${
                    username === msg.username ? "bg-green-600" : "bg-blue-600"
                  } w-2/3 h-12 p-2 text-white m-2 rounded-xl rounded-br-none`}
                >
                  <div>{msg.message}</div>
                  <div className="w-full flex justify-end text-xs">
                    {msg.username}
                  </div>
                </div>
              </div>
            ))}
        </div>
        <div className="absolute bottom-0 left-0 w-full">
          <input
            value={message}
            onChange={(e) => setMessage(e.target.value)}
            className="w-3/4 h-12 border p-3 outline-none"
            type="text"
            placeholder="message send"
            onKeyDown={onKeyPress}
          />
          <button
            onClick={sendMessage}
            className="w-1/4 bg-indigo-600 text-white h-12 hover-opacity-70"
          >
            SEND
          </button>
        </div>
      </div>
    </div>
  );
};

export default Chat;
